import { useState } from "react";
import "./BankPromotionsModal.css";
import { useNavigate } from "react-router-dom";

const BankPromotionsModal = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const toggleModal = () => {
    setIsOpen(!isOpen);
  };

  const handleBack = () => {
    navigate("/Women");
  };
  return (
    <div>
      <div className="bank-promotion-banner">
        <button onClick={toggleModal} className="open-modal-button">
          <span>Up to 9 interest-free installments at Selected Banks</span>
          Promotions
        </button>
      </div>

      {isOpen && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="close-button" onClick={toggleModal}>
              &times;
            </button>

            <h2>Bank Promotions</h2>
            <div className="promotions-grid">
              <div className="promotion">
                <button onClick={handleBack} className="back-button">
                  {" "}
                  Go Back
                </button>
                <img
                  src="data:image/jpeg;base64,"
                  alt="Galicia"
                  className="bank-logo"
                />
                <p>
                  9 interest-free installments with Galicia (Amex, Visa and
                  Mastercard)
                </p>
              </div>
              <div className="promotion">
                <img
                  src="https://www.launionuyc.org.ar/wp-content/uploads/2020/09/santander-rio.jpg"
                  alt="Santander"
                  className="bank-logo"
                />
                <p>
                  3, 6 and 9 interest-free installments with Santander (Visa and
                  Mastercard)
                </p>
              </div>
              <div className="promotion">
                <img
                  src="data:image/png;base64,"
                  alt="ICBC"
                  className="bank-logo"
                />
                <p>
                  3, 6 and 9 interest-free installments with ICBC (Visa and
                  Mastercard)
                </p>
              </div>
              <div className="promotion">
                <img
                  src="data:image/png;base64,"
                  alt="Macro"
                  className="bank-logo"
                />
                <p>
                  Up to 9 interest-free installments with Macro Selecta (Visa
                  and Mastercard)
                </p>
              </div>
              <div className="promotion">
                <img
                  src="data:image/png;base64,"
                  alt="Naranja"
                  className="bank-logo"
                />
                <p>
                  Up to 6 interest-free installments with Naranja (Visa and
                  Mastercard)
                </p>
              </div>
              <div className="promotion">
                <img
                  src="data:image/png;base64,"
                  alt="Banco Ciudad"
                  className="bank-logo"
                />
                <p>
                  Up to 6 interest-free installments with Santander (Visa and
                  Mastercard)
                </p>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BankPromotionsModal;
